import timeprint('-'*70)print('                         Length Converter')def main():        print('-'*70)        time.sleep(2)        userNumber = float(input("Please input the amount of centimeters(cm) "+                             "you would like to convert:"))        print('-'*70)        print('Processing...')        userMeter = round((userNumber/100),5)        userKilometer = round((userNumber/100000),5)        userMillimeter = round((userNumber*10),5)        userInch = round((userNumber/2.54),5)        userFoot = round((userNumber/30.48),5)        print('-'*70)        time.sleep(2)        print('Results:\n')        print('Meters: {}'.format(userMeter))        print('Kilometers: {}'.format(userKilometer))        print('Millimeters: {}'.format(userMillimeter))        print('Inches: {}'.format(userInch))        print('Feet: {}'.format(userFoot))        print('-'*70)        while True:        main()    time.sleep(2)        if str(input("Would you like to convert again?(Y/N)")).strip().upper() != "Y":              print("\nGoodbye!\n")            time.sleep(1)            break